import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import logo from '../assets/logo.png';
import { Navbar, Nav, Form, FormControl, Button, Container, Collapse } from 'react-bootstrap';
import '../css/header.css';

const CinemaHeader: React.FC = () => {
    const [isMenuOpen, setIsMenuOpen] = useState(false);
    const [searchQuery, setSearchQuery] = useState('');
    const location = useLocation();

    // Toggle Menu
    const toggleMenu = () => setIsMenuOpen(!isMenuOpen);

    // Handle Search
    const handleSearch = (e: React.FormEvent) => {
        e.preventDefault();
        console.log('Searching for:', searchQuery);
        // Add search logic here (e.g., API call or navigation)
    };

    const isActive = (path: string) => location.pathname === path;

    return (
        <header className="header bg-dark text-white">
            <Container fluid>
                <Navbar expand="lg" variant="dark" className="w-100">
                    <Navbar.Brand href="/" className="d-flex align-items-center me-auto">
                        <img src={logo} alt="Cinema Logo" style={{ width: '50px' }} className="rounded-circle me-2" />
                        <span className="fw-bold">Cinema</span>
                    </Navbar.Brand>

                    {/* Hamburger Button */}
                    <Navbar.Toggle aria-controls="navbarNav" onClick={toggleMenu} />
                    
                    <Collapse in={isMenuOpen}>
                        <div id="navbarNav" className="w-100">
                            <Nav className="me-auto justify-content-center">
                                <Nav.Link href="/movies" active={isActive('/movies')}>Phim</Nav.Link>
                                <Nav.Link href="/cinemas" active={isActive('/cinemas')}>Rạp</Nav.Link>
                                <Nav.Link href="/showtimes" active={isActive('/showtimes')}>Lịch Chiếu</Nav.Link>
                                <Nav.Link href="/offers" active={isActive('/offers')}>Ưu Đãi</Nav.Link>
                                <Nav.Link href="/contact" active={isActive('/contact')}>Liên Hệ</Nav.Link>
                            </Nav>

                            {/* Search Bar */}
                            <Form className="d-flex" onSubmit={handleSearch}>
                                <FormControl
                                    type="search"
                                    placeholder="Tìm kiếm phim..."
                                    className="me-2"
                                    aria-label="Search"
                                    value={searchQuery}
                                    onChange={(e) => setSearchQuery(e.target.value)}
                                />
                                <Button variant="outline-light" type="submit">
                                    Tìm Kiếm
                                </Button>
                            </Form>
                        </div>
                    </Collapse>
                </Navbar>
            </Container>
        </header>
    );
};

export default CinemaHeader;
