import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';

const CinemaHeader: React.FC = () => {
    const [isMenuOpen, setIsMenuOpen] = useState(false);
    const [searchQuery, setSearchQuery] = useState('');
    const location = useLocation();

    // Toggle Menu
    const toggleMenu = () => setIsMenuOpen(!isMenuOpen);

    // Handle Search
    const handleSearch = (e: React.FormEvent) => {
        e.preventDefault();
        console.log('Searching for:', searchQuery);
        // Logic tìm kiếm (Gọi API hoặc chuyển hướng)
    };

    // Check Active Link
    const isActive = (path: string) => location.pathname === path;

    return (
        <header className="header bg-dark text-white py-3 shadow">
            <div className="container">
                <nav className="navbar navbar-expand-lg navbar-dark">
                    {/* Logo */}
                    <a className="navbar-brand" href="/">
                        <h1 className="m-0">Cinema</h1>
                    </a>

                    {/* Hamburger Button */}
                    <button
                        className="navbar-toggler"
                        type="button"
                        aria-controls="navbarNav"
                        aria-expanded={isMenuOpen ? 'true' : 'false'}
                        aria-label="Toggle navigation"
                        onClick={toggleMenu}
                    >
                        <span className="navbar-toggler-icon"></span>
                    </button>

                    {/* Navigation Menu */}
                    <div
                        className={`collapse navbar-collapse ${isMenuOpen ? 'show' : ''}`}
                        id="navbarNav"
                    >
                        <ul className="navbar-nav ms-auto">
                            <li className={`nav-item ${isActive('/movies') ? 'active' : ''}`}>
                                <a className="nav-link" href="/movies">
                                    Movies
                                </a>
                            </li>
                            <li className={`nav-item ${isActive('/about') ? 'active' : ''}`}>
                                <a className="nav-link" href="/about">
                                    About Us
                                </a>
                            </li>
                            <li className={`nav-item ${isActive('/contact') ? 'active' : ''}`}>
                                <a className="nav-link" href="/contact">
                                    Contact
                                </a>
                            </li>
                        </ul>
                    </div>

                    {/* Search Bar */}
                    <form className="d-flex ms-3" onSubmit={handleSearch}>
                        <input
                            className="form-control me-2"
                            type="search"
                            placeholder="Search movies..."
                            aria-label="Search"
                            value={searchQuery}
                            onChange={(e) => setSearchQuery(e.target.value)}
                        />
                        <button className="btn btn-outline-light" type="submit">
                            Search
                        </button>
                    </form>
                </nav>
            </div>
        </header>
    );
};

export default CinemaHeader;
