import React, { useState } from 'react';
import Layout from '../containers/Layout_User';
import { Carousel, Card, Button } from 'react-bootstrap';
import '../styles/user/Home.scss';
import { movies } from '../mock/mockData';

import banner1 from '../assets/banner1.png';
import banner2 from '../assets/banner2.png';
import banner3 from '../assets/banner3.png';

const HomePage: React.FC = () => {
    const [index, setIndex] = useState(0);

    const handleSelect = (selectedIndex: number) => {
        setIndex(selectedIndex);
    };

    const banners = [
        { src: banner1, alt: 'Banner 1', title: 'Banner 1', description: 'Đây là nội dung của Banner 1.' },
        { src: banner2, alt: 'Banner 2', title: 'Banner 2', description: 'Đây là nội dung của Banner 2.' },
        { src: banner3, alt: 'Banner 3', title: 'Banner 3', description: 'Đây là nội dung của Banner 3.' },
    ];

    return (
        <Layout>
            <div className="container">
                <section className="carousel-section">
                    <Carousel
                        activeIndex={index}
                        onSelect={handleSelect}
                        controls={false}
                        indicators={true}
                        interval={5000}
                        fade
                    >
                        {banners.map((banner, idx) => (
                            <Carousel.Item key={idx}>
                                <img
                                    className="d-block w-100"
                                    src={banner.src}
                                    alt={banner.alt}
                                />
                            </Carousel.Item>
                        ))}
                    </Carousel>
                </section>

                <section className="movies-section mt-4">
                    <h4 className="text-center"><strong>CÁC PHIM ĐANG CHIẾU</strong></h4>
                    <div className="row">
                        {movies.map(movie => {
                            // Hàm giới hạn số từ của description
                            const truncateDescription = (text: string, wordLimit: number): string => {
                                const words = text.split(' ');
                                if (words.length > wordLimit) {
                                    return words.slice(0, wordLimit).join(' ') + '...';
                                }
                                return text;
                            };

                            // Hàm giới hạn số ký tự của title
                            const truncateTitle = (text: string, charLimit: number): string => {
                                if (text.length > charLimit) {
                                    return text.slice(0, charLimit) + '...';
                                }
                                return text;
                            };

                            return (
                                <div className="col-xxl-3 col-xl-3 col-lg-3 col-md-4 col-sm-6 col-6 mb-4" key={movie.id}>
                                    <div className="cinema-card">
                                        <img className="card-img-top" src={movie.image} alt={movie.title} />
                                        <div className="card-body text-center mx-auto d-flex">
                                            <h5 className="card-title">{truncateTitle(movie.title, 20)}</h5>
                                            <p className="card-text">{truncateDescription(movie.description, 10)}</p>
                                            <button className="btn-primary">Trailer</button>
                                            <button className="btn-details">Chi tiết</button>
                                            <button className="btn-book">Đặt ngay</button>
                                        </div>
                                    </div>
                                </div>
                            );
                        })}
                    </div>
                </section>
            </div>
        </Layout>
    );
};

export default HomePage;
