import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import logo from '../assets/logo.png';
import { Navbar, Nav, Form, FormControl, Button, Container, Offcanvas } from 'react-bootstrap';
import NavDropdown from 'react-bootstrap/NavDropdown';
import '../css/header.css';

const CinemaHeader: React.FC = () => {
    const [isMenuOpen, setIsMenuOpen] = useState(false);
    const [searchQuery, setSearchQuery] = useState('');
    const location = useLocation();

    // Toggle Menu
    const toggleMenu = () => setIsMenuOpen(!isMenuOpen);

    // Handle Search
    const handleSearch = (e: React.FormEvent) => {
        e.preventDefault();
        console.log('Searching for:', searchQuery);
        // Add search logic here (e.g., API call or navigation)
    };

    const isActive = (path: string) => location.pathname === path;

    return (
        <Navbar expand="lg" className="bg-body-tertiary mb-3">
            <Container fluid>
                <Navbar.Brand href="/">
                    <img src={logo} alt="Cinema Logo" style={{ width: '50px' }} className="rounded-circle me-2" />
                    <span className="fw-bold">Cinema</span>
                </Navbar.Brand>
                <Navbar.Toggle aria-controls="offcanvasNavbar" onClick={toggleMenu} />
                <Offcanvas
                    id="offcanvasNavbar"
                    aria-labelledby="offcanvasNavbarLabel"
                    placement="end"
                    show={isMenuOpen}
                    onHide={toggleMenu}
                >
                    <Offcanvas.Header closeButton>
                        <Offcanvas.Title id="offcanvasNavbarLabel">Navigation</Offcanvas.Title>
                    </Offcanvas.Header>
                    <Offcanvas.Body>
                        <Nav className="justify-content-end flex-grow-1 pe-3">
                            <Nav.Link href="/movies" active={isActive('/movies')}>Phim</Nav.Link>
                            <Nav.Link href="/cinemas" active={isActive('/cinemas')}>Rạp</Nav.Link>
                            <Nav.Link href="/showtimes" active={isActive('/showtimes')}>Lịch Chiếu</Nav.Link>
                            <Nav.Link href="/offers" active={isActive('/offers')}>Ưu Đãi</Nav.Link>
                            <Nav.Link href="/contact" active={isActive('/contact')}>Liên Hệ</Nav.Link>
                            <NavDropdown title="Dropdown" id="offcanvasNavbarDropdown">
                                <NavDropdown.Item href="#action3">Action</NavDropdown.Item>
                                <NavDropdown.Item href="#action4">Another action</NavDropdown.Item>
                                <NavDropdown.Divider />
                                <NavDropdown.Item href="#action5">Something else here</NavDropdown.Item>
                            </NavDropdown>
                        </Nav>
                        <Form className="d-flex">
                            <FormControl
                                type="search"
                                placeholder="Search"
                                className="me-2"
                                aria-label="Search"
                                value={searchQuery}
                                onChange={(e) => setSearchQuery(e.target.value)}
                            />
                            <Button variant="outline-success" type="submit" onClick={handleSearch}>
                                Search
                            </Button>
                        </Form>
                    </Offcanvas.Body>
                </Offcanvas>
            </Container>
        </Navbar>
    );
};

export default CinemaHeader;
