import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import logo from '../assets/logo.png';
import '../css/header.css';

const CinemaHeader: React.FC = () => {
    const [isMenuOpen, setIsMenuOpen] = useState(false);
    const [isUserMenuOpen, setIsUserMenuOpen] = useState(false);
    const [isSearchOpen, setIsSearchOpen] = useState(false);
    const [searchQuery, setSearchQuery] = useState('');
    const location = useLocation();

    // Toggle Menu
    const toggleMenu = () => setIsMenuOpen(!isMenuOpen);

    // Toggle User Dropdown Menu
    const toggleUserMenu = () => setIsUserMenuOpen(!isUserMenuOpen);

    // Toggle Search Bar
    const toggleSearch = () => setIsSearchOpen(!isSearchOpen);

    // Handle Search
    const handleSearch = (e: React.FormEvent) => {
        e.preventDefault();
        console.log('Searching for:', searchQuery);
        // Add search logic here (e.g., API call or navigation)
    };

    const isActive = (path: string) => location.pathname === path;

    return (
        <header className="header bg-dark text-white">
            <div className="container-fluid">
                <nav className="navbar navbar-expand-lg navbar-dark">
                    <div className="d-flex align-items-center w-100">
                        {/* Logo */}
                        <a className="navbar-brand d-flex align-items-center me-auto" href="/">
                            <img src={logo} alt="Cinema Logo" style={{ width: '50px' }} className="rounded-circle me-2" />
                            <span className="fw-bold">Cinema</span>
                        </a>

                        {/* Navigation Links */}
                        <div className="collapse navbar-collapse justify-content-center" id="navbarNav">
                            <ul className="navbar-nav">
                                <li className={`nav-item ${isActive('/movies') ? 'active' : ''}`}>
                                    <a className="nav-link" href="/movies">Phim</a>
                                </li>
                                <li className={`nav-item ${isActive('/cinemas') ? 'active' : ''}`}>
                                    <a className="nav-link" href="/cinemas">Rạp</a>
                                </li>
                                <li className={`nav-item ${isActive('/showtimes') ? 'active' : ''}`}>
                                    <a className="nav-link" href="/showtimes">Lịch Chiếu</a>
                                </li>
                                <li className={`nav-item ${isActive('/offers') ? 'active' : ''}`}>
                                    <a className="nav-link" href="/offers">Ưu Đãi</a>
                                </li>
                                <li className={`nav-item ${isActive('/contact') ? 'active' : ''}`}>
                                    <a className="nav-link" href="/contact">Liên Hệ</a>
                                </li>
                            </ul>
                        </div>

                        {/* Search Bar for Desktop */}
                        <form className="d-flex ms-3 d-none d-lg-flex" onSubmit={handleSearch}>
                            <input
                                className="form-control me-2"
                                type="search"
                                placeholder="Tìm kiếm phim..."
                                aria-label="Search"
                                value={searchQuery}
                                onChange={(e) => setSearchQuery(e.target.value)}
                            />
                            <button className="btn btn-outline-light" type="submit">
                                Tìm Kiếm
                            </button>
                        </form>

                        {/* User Account Dropdown */}
                        <div className="nav-item dropdown ms-3">
                            <a
                                className="nav-link dropdown-toggle"
                                href="#"
                                id="userMenu"
                                role="button"
                                data-bs-toggle="dropdown"
                                aria-expanded={isUserMenuOpen ? 'true' : 'false'}
                                onClick={toggleUserMenu}
                            >
                                Tài Khoản
                            </a>
                            <ul className={`dropdown-menu ${isUserMenuOpen ? 'show' : ''}`} aria-labelledby="userMenu">
                                <li>
                                    <a className="dropdown-item" href="/login">Đăng Nhập</a>
                                </li>
                                <li>
                                    <a className="dropdown-item" href="/register">Đăng Ký</a>
                                </li>
                            </ul>
                        </div>

                        {/* Hamburger Button for Mobile */}
                        <button
                            className="navbar-toggler ms-2"
                            type="button"
                            aria-controls="navbarNav"
                            aria-expanded={isMenuOpen ? 'true' : 'false'}
                            aria-label="Toggle navigation"
                            onClick={toggleMenu}
                        >
                            <span className="navbar-toggler-icon"></span>
                        </button>
                    </div>

                    {/* Search Bar for Mobile */}
                    {isSearchOpen && (
                        <form className="d-flex ms-3 mt-3 mt-lg-0 d-lg-none" onSubmit={handleSearch}>
                            <input
                                className="form-control me-2"
                                type="search"
                                placeholder="Tìm kiếm phim..."
                                aria-label="Search"
                                value={searchQuery}
                                onChange={(e) => setSearchQuery(e.target.value)}
                            />
                            <button className="btn btn-outline-light" type="submit">
                                Tìm Kiếm
                            </button>
                        </form>
                    )}
                </nav>
            </div>
        </header>
    );
};

export default CinemaHeader;
