import React, { useState, useEffect } from 'react';
import Layout from '../components/Layout';
import { Carousel, Button, Card } from 'react-bootstrap';
import { fetchMovies } from '../services/api'; // Import hàm fetchMovies
import { Movie } from '../types'; // Import Movie interface
import '../Scss/Home.scss';

const HomePage: React.FC = () => {
    const [index, setIndex] = useState(0);
    const [movies, setMovies] = useState<Movie[]>([]); // State để lưu danh sách phim

    useEffect(() => {
        const getMovies = async () => {
            const movieData = await fetchMovies();
            setMovies(movieData); // Cập nhật danh sách phim
        };
        getMovies();
    }, []);

    const handleSelect = (selectedIndex: number) => {
        setIndex(selectedIndex);
    };

    const banners = [
        { src: '../assets/banner1.jpg', alt: 'Banner 1' },
        { src: '../assets/banner2.jpg', alt: 'Banner 2' },
        { src: '../assets/banner3.jpg', alt: 'Banner 3' },
    ];

    return (
        <Layout>
            <div className="container">
                <section className="carousel-section">
                    <Carousel activeIndex={index} onSelect={handleSelect} controls={false} indicators={true} interval={5000} fade>
                        {banners.map((banner, idx) => (
                            <Carousel.Item key={idx}>
                                <img className="d-block w-100" src={banner.src} alt={banner.alt} />
                            </Carousel.Item>
                        ))}
                    </Carousel>
                </section>

                <section className='movie-section'>
                    <h1 className='text-center mt-2'>PHIM ĐANG CHIẾU</h1>
                    <div className="movie-cards">
                        {movies.map((movie) => (
                            <Card key={movie.id} style={{ width: '18rem' }}>
                                <Card.Img variant="top" src={movie.posterUrl} />
                                <Card.Body>
                                    <Card.Title>{movie.title}</Card.Title>
                                    <Card.Text>{movie.description}</Card.Text>
                                    <Button variant="primary">Xem Chi Tiết</Button>
                                </Card.Body>
                            </Card>
                        ))}
                    </div>
                </section>
            </div>
        </Layout>
    );
};

export default HomePage;
