import React from "react";
import { Container, Row, Col } from "react-bootstrap";

const Footer: React.FC = () => {
    return (
        <footer className="footer" style={{ backgroundColor: "#1b2735", padding: "20px 0" }}>
            <Container>
                <Row>
                    {/* Left column */}
                    <Col md={6} className="text-white">
                        <p>&copy; 2021 CinemaSystem</p>
                    </Col>

                    {/* Right column with links */}
                    <Col md={6} className="text-white text-md-end">
                        <p>Follow us:</p>
                        <a href="#" className="text-white me-3">Facebook</a>
                        <a href="#" className="text-white me-3">Instagram</a>
                        <a href="#" className="text-white">Twitter</a>
                    </Col>
                </Row>
            </Container>
        </footer>
    );
};

export default Footer;
